package week1.day1;

public class LearnFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for (int i=0; i<=50; i++) {
	if (i%2==0) {
		System.out.println("The given number is " + i + " even");
	}
	else
	{
		System.out.println("The given number is " + i + " odd");
	}
}
	}
}
